frontMatter.style:
body {
  margin: 0
} .slide.slide--highlighted h3 {
  color: white;
} .no-title h2 {
  display: none;
} .slide .shout.shout--small {
  font-size: 90px;
} .slide h2.shout--highlighted {
  color: #38bcc2;
} .timeline {
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;

  margin-top: 100px;
} .timeline__item {
  position: relative;

  background: transparent;
  padding: 5px;
  width: 255px;
  height: 255px;
  margin: 5px;

  border: tomato 3px solid;

  line-height: 255px;
  font-weight: bold;
  font-size: 5em;
  text-align: center;
} .timeline__item-name {
  position: absolute;
  top: -16px;
  left: 75px;

  width: 100px;

  font-size: 24px;
  line-height: 32px;

  background: white;
  border: tomato 3px solid;
} .timeline__item--position-1 {
  order: 1;
} .timeline__item--position-2 {
  order: 2;
} .timeline__item--position-3 {
  order: 3;
} .timeline__item-icon {
  width: 150px;
  margin: 52px;
  margin-top: 45px;
} .team-top {
  animation-name: fade;
  animation-timing-function: ease-in-out;
  animation-iteration-count: infinite;
  animation-duration: 3s;
  animation-direction: alternate;
} @keyframes fade {
  0% {
    opacity: 1;
  }
  25% {
    opacity: 1;
  }
  75% {
    opacity: 0;
  }
  100% {
    opacity: 0;
  }
} .space-label {
  width: 340px;
  margin: 0 auto;
  position: absolute;
  top: 50px;
  right: 0;
  line-height: 1em;
  transform: rotate(15deg);
} .space-label .top, .space-label .bottom {
  height: 60px;
  overflow: hidden;
  position: relative;
  width: 170px;
  margin: 0 auto;
} .space-label .top .ring, .space-label .bottom .ring {
  border-radius: 100%;
  position: absolute;
  width: 170px;
  height: 170px;
  border: 3px solid #999;
  border-width: 8px;
} .space-label .top .ring:nth-child(2), .space-label .bottom .ring:nth-child(2) {
  border-width: 4px;
  width: 154px;
} .space-label .top .ring:nth-child(3), .space-label .bottom .ring:nth-child(3) {
  border-width: 2px;
  width: 139px;
} .space-label .top .ring:nth-child(3):after, .space-label .bottom .ring:nth-child(3):after {
  content: "";
  width: 18px;
  position: absolute;
  border-top: 1px solid #999;
  left: 50%;
  margin-left: -9px;
} .space-label .top .ring:nth-child(3):before, .space-label .bottom .ring:nth-child(3):before {
  content: "";
  width: 32px;
  position: absolute;
  border-top: 1px solid #999;
  margin-left: -16px;
  left: 50%;
} .space-label .top .ring:nth-child(1) {
  top: 0;
  left: 0;
} .space-label .top .ring:nth-child(2) {
  top: 10px;
  left: 8px;
} .space-label .top .ring:nth-child(3) {
  top: 20px;
  left: 16px;
} .space-label .top .ring:nth-child(3):after {
  top: 17px;
} .space-label .top .ring:nth-child(3):before {
  top: 22px;
} .space-label .bottom .ring:nth-child(1) {
  bottom: 0;
  left: 0;
} .space-label .bottom .ring:nth-child(2) {
  bottom: 10px;
  left: 8px;
} .space-label .bottom .ring:nth-child(3) {
  bottom: 20px;
  left: 16px;
} .space-label .bottom .ring:nth-child(3):after {
  bottom: 17px;
} .space-label .bottom .ring:nth-child(3):before {
  bottom: 22px;
} .space-label .text {
  text-align: center;
  text-transform: uppercase;
  font-family: "Raleway", sans-serif;
  font-size: 42px;
  font-weight: 900;
  color: #999;
  height: 55px;
  vertical-align: middle;
  display: table-cell;
  width: 340px;
  position: relative;
  left: 7px;
  letter-spacing: 14px;
}
  #chart {
  position: relative;
  background-color: #fff;
  width: 700px;
  height: 700px;
  margin: 0 auto;
}
#card_container {
  position: absolute;
  width: 450px;
  height: 450px;
  background-color: #fff;
  border: 1px solid orange;
  border-radius: 50%;
  display: none;
  font-family: sans-serif;
  font-size: 20px;
  pointer-events: none;
}
#card {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
}
#card h2, #card p {
  margin: 0;
}
#name {
  font-family: Verlag;
  font-weight: bold;
  padding-bottom: 1rem;
}
#team {
  font-size: 16px;
}
#sub_team, #responsibility {
  font-size: 14px;
  font-style: italic;
}
.arc:hover {
  fill: #0019AF;
}
.circle-person:hover {
  fill: orange;
}
text {
  font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
  font-size: 12px;
  fill: #fff;
} .slide h2.shout--rainbow {
  /* Chrome, Safari, Opera */
  -webkit-animation: rainbow 5s infinite; 

  /* Internet Explorer */
  -ms-animation: rainbow 5s infinite;

  /* Standar Syntax */
  animation: rainbow 5s infinite; 
} /* Chrome, Safari, Opera */ @-webkit-keyframes rainbow{
  0%{color: orange;}	
  10%{color: purple;}	
  20%{color: red;}
  30%{color: CadetBlue;}
  40%{color: yellow;}
  50%{color: coral;}
  60%{color: green;}
  70%{color: cyan;}
  80%{color: DeepPink;}
  90%{color: DodgerBlue;}
  100%{color: orange;}
}
/* Internet Explorer */ @-ms-keyframes rainbow{
  0%{color: orange;}	
  10%{color: purple;}	
  20%{color: red;}
  30%{color: CadetBlue;}
  40%{color: yellow;}
  50%{color: coral;}
  60%{color: green;}
  70%{color: cyan;}
  80%{color: DeepPink;}
  90%{color: DodgerBlue;}
  100%{color: orange;}
}
/* Standar Syntax */ @keyframes rainbow{
    0%{color: orange;}	
  10%{color: purple;}	
  20%{color: red;}
  30%{color: CadetBlue;}
  40%{color: yellow;}
  50%{color: coral;}
  60%{color: green;}
  70%{color: cyan;}
  80%{color: DeepPink;}
  90%{color: DodgerBlue;}
  100%{color: orange;}
}
